import React from 'react'
import {connect} from 'react-redux'
import {postListing} from '../actions'

class Post extends React.Component {
    constructor(props){
        super(props);

        this.state = {
            newItem: {
                "sleepDate": "",
                "wakeDate": "",
                "sleepTime": "",
                "wakeTime": "",
                "moodBefore": null,
                "moodAfter": null,
                "moodDuring": null
            }
        }

    }

    handleAddChange = (e) => {
        this.setState({
            newItem: {
                ...this.state.newItem,
                [e.target.name]: e.target.value,
            }
        })
    }


    newPost = (e) => {
        e.preventDefault();
        this.props.postListing(this.state.newItem);

        this.setState({
            newItem: {
                "sleepDate": "",
                "wakeDate": "",
                "sleepTime": "",
                "wakeTime": "",
                "moodBefore": null,
                "moodAfter": null,
                "moodDuring": null
            }
        })

    }
    render(){
        return(
        <div className='postContainer'>
           <h1>Post New Listing</h1>

           <form className="postForm" onSubmit={(e) => this.newPost(e)}>
                    <input 
                        type = 'date'
                        name = 'sleepDate'
                        placeholder = 'Sleep Date: MM-DD-YYYY'
                        onChange = {(e) => this.handleAddChange(e)}
                        value = {this.state.newItem.sleepDate}
                    />
                    <input 
                        type = 'date'
                        name = 'wakeDate'
                        placeholder = 'Wake Date: MM-DD-YYYY'
                        onChange = {(e) => this.handleAddChange(e)}
                        value = {this.state.newItem.wakeDate}
                    />
                    <input 
                        type = 'time'
                        name = 'sleepTime'
                        placeholder = 'Sleep Time: HH:MM AM/PM'
                        onChange = {(e) => this.handleAddChange(e)}
                        value = {this.state.newItem.sleepTime}
                    />
                    <input 
                        type = 'time'
                        name = 'wakeTime'
                        placeholder = 'Wake Time: HH:MM AM/PM'
                        onChange = {(e) => this.handleAddChange(e)}
                        value = {this.state.newItem.wakeTime}
                    />
                    {/* face input */}
                    <div>
                    <input 
                        type = 'radio'
                        name = 'moodBefore'
                        placeholder = 'Mood Before 1-5'
                        onChange = {(e) => this.handleAddChange(e)}
                        value = {1}
                    />
                     <input 
                        type = 'radio'
                        name = 'moodBefore'
                        placeholder = 'Mood Before 1-5'
                        onChange = {(e) => this.handleAddChange(e)}
                        value = {2}
                    />
                     <input 
                        type = 'radio'
                        name = 'moodBefore'
                        placeholder = 'Mood Before 1-5'
                        onChange = {(e) => this.handleAddChange(e)}
                        value = {3}
                    />
                     <input 
                        type = 'radio'
                        name = 'moodBefore'
                        placeholder = 'Mood Before 1-5'
                        onChange = {(e) => this.handleAddChange(e)}
                        value = {4}
                    />
                    <input 
                        type = 'radio'
                        name = 'moodBefore'
                        placeholder = 'Mood Before 1-5'
                        onChange = {(e) => this.handleAddChange(e)}
                        value = {5}
                    />

                    </div>
                    {/* face input */}
                     <input 
                        type = 'radio'
                        name = 'moodDuring'
                        placeholder = 'Mood During 1-5'
                        onChange = {(e) => this.handleAddChange(e)}
                        value = {this.state.newItem.moodDuring}
                    />

                      <input 
                        type = 'radio'
                        name = 'moodAfter'
                        placeholder = 'Mood After 1-5'
                        onChange = {(e) => this.handleAddChange(e)}
                        value = {this.state.newItem.moodAfter}
                    />


                    <button className ='postButton'>Post</button>
                </form>

        </div>
        )
    }
}
const mapStateToProps = (state) => {
    return({
      ...state,
      user_id: state.auth.user_id,
      auth: {...state.auth,} 

    })
  }

  export default connect(mapStateToProps, {postListing})(Post);
