import React from 'react'
import {connect} from 'react-redux'
import {getItems} from '../actions';

class Listing extends React.Component {

    componentDidMount(){
        this.props.getItems()
    }

    render(){
        return(
        <div className = 'listingContainer'>
            {this.props.tech.map((i) => {
                return(
                <div className='item'>
                    <img src={i.picture} alt='its a penis' />
                    <div className='line'></div>
                    <h3>Item:</h3>
                    <p>{i.name}</p>
                    <h3>Description:</h3>
                    <p>{i.description}</p>
                    <h3>Price:</h3>
                    <p>${i.cost}</p>
                    <h3>Posted By:</h3>
                    <p>{i.user}</p>

                    <button className='deleteButton'>Delete Item</button>
                </div>      
                )
            })}
         </div>
        )
    }
}
const mapStateToProps = (state) => {
    return({
      ...state,
      tech: state.tech,

    })
  }

  export default connect(mapStateToProps, {getItems})(Listing);
