import React from 'react'
import {connect} from 'react-redux'

class Edit extends React.Component {

    constructor(props){
        super(props);

        // this.state = {
        //     editedItem: {
        //         id: this.props.passedItem.id,
        //         name: this.props.passedItem.name,
        //         cost: this.props.passedItem.cost,
        //         category: this.props.passedItem.category,
        //         description: this.props.passedItem.description,
        //         picture: this.props.passedItem.picture,
        //         availability: this.props.passedItem.availability,
        //     }
        }

        componentDidMount(){
            this.setState({
                    editedItem: {
                        id: this.props.passedItem.id,
                        name: this.props.passedItem.name,
                        cost: this.props.passedItem.cost,
                        category: this.props.passedItem.category,
                        description: this.props.passedItem.description,
                        picture: this.props.passedItem.picture,
                        availability: this.props.passedItem.availability,
                    }
                })

        }

    handleEdit = (e) => {
        this.setState({
            editedItem: {
                ...this.state.newItem,
                [e.target.name]: e.target.value,
            }
        })
    }


    editPost = (e) => {
        e.preventDefault();
        this.props.postListing(this.state.newItem);

        this.setState({
            editedItem: {
                item: '',
                cost: '',
                category: '',
                description: '',
                picture: '',
                availability: true,
            }
        })

    }
    render(){
        return(
        <div className='postContainer'>
           <h1>Edit Post Listing</h1>

           <form className="postForm" onSubmit={(e) => this.editPost(e)}>
                    <input 
                        type = 'text'
                        name = 'name'
                        placeholder = 'Item Name'
                        onChange = {(e) => this.handleEdit(e)}
                        value = {this.state.editedItem.name}
                    />
                    <input 
                        type = 'text'
                        name = 'category'
                        placeholder = 'Category'
                        onChange = {(e) => this.handleEdit(e)}
                        value = {this.state.editedItem.category}
                    />
                    <input 
                        type = 'text'
                        name = 'cost'
                        placeholder = 'Cost To Rent'
                        onChange = {(e) => this.handleEdit(e)}
                        value = {this.state.editedItemcost}
                    />
                    <input 
                        type = 'text'
                        name = 'description'
                        placeholder = 'Item Description'
                        onChange = {(e) => this.handleEdit(e)}
                        value = {this.state.editedItemdescription}
                    />
                    <input 
                        type = 'text'
                        name = 'picture'
                        placeholder = 'Image URL'
                        onChange = {(e) => this.handleEdit(e)}
                        value = {this.state.editedItem.picture}
                    />

                    <button className ='postButton'>Post</button>
                </form>

        </div>
        )
    }
}
const mapStateToProps = (state) => {
    return({
      ...state,
      user_id: state.auth.user_id,
      auth: {...state.auth,} ,
      passedItem: state.passedItem,

    })
  }

  export default connect(mapStateToProps, {})(Edit);
