import {SENDING_CRED, SENDING_CRED_SUCCESS, SENDING_CRED_FAIL} from '../actions'

const initialState = {
    sending_login_creds: false,
    auth: [{
        token: '',
        user_id: '',
        message: '',
        user_name: '',
    }],
    err: '',
}

export const reducer = (state = initialState, action) => {
    switch(action.type){
        case SENDING_CRED:
            return({
                ...state,
                sending_login_creds: true,
            })
        case SENDING_CRED_SUCCESS:
            return({
                ...state,
                sending_login_creds: false,
                auth: action.payload,
            })
        case SENDING_CRED_FAIL:
            return({
                ...state,
                sending_login_creds: false,
                err: action.payload,
            })
        default:
            return({
                ...state,
            })
    }

}
